{#if paths[type]}
  <svg
    class="ons-svg-icon"
    class:ons-u-ml-xs="{marginLeft}"
    class:ons-u-mr-xs="{marginRight}"
    class:selected="{selected}"
    viewBox="{paths[type].viewBox}"
    xmlns="http://www.w3.org/2000/svg"
    focusable="false"
    fill="{selected ? '#206095' : 'currentColor'}"
  >
    {#if type != "radius"}
      <path d="{paths[type].d}"></path>
    {:else if type == "radius"}
      <circle cx="16" cy="16" r="12" stroke="currentColor" stroke-width="2" fill="none"></circle>
    {/if}
  </svg>
{/if}

<style>
  .selected {
    /* opacity: 0.2; */
    fill: #206095;
  }
</style>
